mapper.select:
SELECT ilecture_room ilectureRoom , lecture_room_name lectureRoomName , building_name buildingName , max_capacity maxCapacity, del_yn delYn from lecture_room
and lecture_room_name = #{lectureRoomName}
and building_name = #{buildingName}
LIMIT #{staIdx}, 10

mapper.update:
UPDATE lecture_room
SET del_yn = 1
WHERE ilecture_room = #{ilectureRoom}
