mapper.select:
SELECT imajor, major_name majorName
  , graduation_score graduationScore, del_yn delYn, remarks remarks
  from major


          and major_name = #{majorName}


          and del_yn =#{delYn}


  LIMIT #{staIdx}, 10

mapper.update:
UPDATE major
SET del_yn = 1
WHERE imajor = #{imajor}

mapper.select:
SELECT count(*)
from major


        and major_name = #{majorName}


        and del_yn = #{delYn}

