mapper.select:
SELECT ilecture,D.lecture_name lectureNm,isemester,C.nm,B.lecture_room_name lectureRoomNm,B.building_name buildingNm,lecture_str_date strDate,lecture_end_date endDate,lecture_str_time strTime,lecture_end_time endTime ,lecture_max_people maxPeople,opening_procedures procedures,A.del_yn delYn from lecture_applly A INNER JOIN lecture_room B ON A.ilecture_room=B.ilecture_room INNER JOIN professor C ON A.iprofessor=C.iprofessor INNER JOIN lecture_name D ON A.ilecture_name=D.ilecture_name
A.opening_procedures=#{procedures}
and C.nm in (#{nm})
limit #{strIdx},#{row}

select count(*) from lecture_applly A inner join professor B on A.iprofessor=B.iprofessor
A.opening_procedures=#{procedures}
and B.nm in (#{nm})

mapper.update:
update lecture_applly
set opening_procedures = #{procedures}
where ilecture=#{ilecture}
