mapper.select:
SELECT A.grade grade,B.semester semester, D.lecture_name lectureName, E.nm professorName, D.score lectureScore, F.total_score totalScore, A.rating rating, A.grade grade, B.semester semester, A.avg_score avgScore, A.rating avgRating FROM student_semester_score A INNER JOIN semester B ON A.isemester = B.isemester INNER JOIN lecture_applly C ON B.isemester = C.isemester INNER JOIN lecture_name D ON C.ilecture_name = D.ilecture_name INNER JOIN professor E ON C.iprofessor = E.iprofessor INNER JOIN lecture_student F ON C.ilecture = F.ilecture INNER JOIN student G ON A.istudent = G.istudent
and B.semester = #{semester}
and G.student_num = #{studentNum}
and A.grade = #{grade}
LIMIT #{staIdx}, 7

SELECT A.grade grade ,C.isemester isemester , B.ilecture ilecture, A.student_num studentNum , C.avg_score avgScore, avg(C.rating) rating FROM student A INNER JOIN lecture_student B ON A.istudent = B.istudent INNER JOIN student_semester_score C ON B.istudent = C.istudent INNER JOIN semester D ON C.isemester = D.isemester
and D.semester = #{semester}
and A.student_num = #{studentNum}
and A.grade = #{grade}

SELECT B.istudent istudent, B.ilecture ilecture , A.imajor imajor, A.student_num studentNum, A.nm name, A.gender gender, A.birthdate birthDate , C.score score, C.rating, A.grade grade ,C.isemester isemester , B.ilecture ilecture, A.student_num studentNum , avg(C.avg_score) avgScore, avg(C.rating) rating FROM student A INNER JOIN lecture_student B ON A.istudent = B.istudent INNER JOIN student_semester_score C ON B.istudent = C.istudent INNER JOIN semester D ON C.isemester = D.isemester
and D.semester = #{semester}
and A.student_num = #{studentNum}
and A.grade = #{grade}
LIMIT #{staIdx}, 7
