mapper.select:
SELECT iprofessor,imajor,professor_password password, nm name, gender,birthdate,phone,email,address
FROM professor
WHERE iprofessor = #{iprofessor}

mapper.update:
update professor
set professor_password = #{password}
where iprofessor = #{iprofessor}

update professor
set imajor = #{imajor}
,phone = #{phone}
,email = #{email}
,address = #{address}
where iprofessor = #{iprofessor}

update professor
set pic = #{pic}
where iprofessor = #{iprofessor}

mapper.select:
SELECT COUNT(*)
FROM lecture_applly
WHERE iprofessor = #{iprofessor}
